import { useState } from 'react';
import './App.css';
import Form1 from './form1';
import Form2 from './form2';
function App() {
    const [form,setForm] = useState(false);
  return (
    <div className='bg-slate-200 w-full h-[100vh] flex items-center justify-center'>
        <div className='h-[80%] w-[40%] bg-white p-8 drop-shadow-md rounded-lg flex flex-col items-center gap-12  '>
            <h1 className='text-[36px] font-semibold text-neutral-900'>Bank Mangement</h1>
            {!form ? <Form1 setForm={setForm}></Form1> : <Form2 setForm={setForm}></Form2>}
        </div>

    </div>
  )
}

export default App;
