import { useState } from 'react';
import './App.css';

function App() {




    const [form,setForm] = useState(false);
    const form1 =<> </>;

     const form2 = <>
            <label  for='mail'>Email</label>
            <input id='mail' name='mail' className='ring-1 ring-neutral-500 rounded-sm outline-none px-2 py-1 text-[16px] text-neutral-700 '></input>
     </>
  return (
    <div className='bg-slate-200 w-full h-[100vh] flex items-center justify-center'>
        <div className='h-[80%] w-[40%] bg-white p-8 drop-shadow-md rounded-lg flex flex-col items-center gap-12  '>
            <h1 className='text-[36px] font-semibold text-neutral-900'>Bank Mangement</h1>
            <form className='text-neutral-800 transition-all duration-300 font-regular text-[18px] px-8 w-full flex flex-col items-center gap-6'>
                {

                    form ? form2 : form1
                }
               <button className='py-2 px-6 bg-neutral-800 hover:bg-neutral-900 transition-all duration-300  text-white rounded-md mt-4 w-full'> {form ?  "View Account" : "Add Account"   }  </button>
               <div className='text-[16px] text-neutral-600 flex justify-center ml-6 gap-5 w-full'>
                <button type='button' className='hover:text-neutral-800' onClick={()=>setForm(!form)}>{form ?   "Add Account" : "View Account" } </button>
                -
                <button type='button'  className='hover:text-neutral-800'>View All Accounts </button>
               </div>
            </form>
        </div>

    </div>
  )
}

export default App;
